## 线段树区域加模板

```C++
# include <bits/stdc++.h>
# define ls (p << 1)
# define rs (p << 1 | 1)


using namespace std;

const int MAXN = 100010;
int n;
typedef long long ll;

int a[MAXN + 2];

struct tree{
    int l, r;
    ll pre, add;
}t[4 * MAXN + 2];

inline void build(int p, int l, int r){
    t[p].l = l; t[p].r = r;
    if (l == r){
        t[p].pre = a[l];
        return ;
    }
    int mid = l + r >> 1;
    build (ls, l, mid);
    build (rs, mid + 1, r);
    t[p].pre = t[ls].pre + t[rs].pre;
}

inline void spread(int p){ // 向下传递懒标记
    if(t[p].add){
        t[ls].pre += t[p].add * (t[ls].r - t[ls].l + 1);
        t[rs].pre += t[p].add * (t[rs].r - t[rs].l + 1);
        t[ls].add += t[p].add;
        t[rs].add += t[p].add;
        t[p].add = 0;
    }
}

inline void change(int p, int x, int y, int z){
    if(x <= t[p].l && y >= t[p].r){  // 覆盖
        t[p].pre += (ll)z * (t[p].r - t[p].l + 1);
        t[p].add += z;
        return ;
    }
    spread(p);
    int mid = t[p].l + t[p].r >> 1;
    if(x <= mid ) change (ls, x, y, z);
    if(y > mid ) change (rs, x, y, z);
    t[p].pre = t[ls].pre + t[rs].pre;
}

inline ll ask(int p, int x, int y){
    if(x <= t[p].l && y >= t[p].r) return t[p].pre;
    spread(p);
    int mid = t[p].l + t[p].r >> 1;
    ll ans = 0;
    if( x <= mid ) ans += ask(ls, x, y);
    if( y > mid ) ans += ask(rs, x, y);
    return ans;
}

int main(){
    ios::sync_with_stdio(false);
    int n, m;
    cin >> n >> m;
    int s, x, y, k;
    for(int i = 1; i <= n; i ++) cin >> a[i];
    build(1, 1, n);
    while ( m -- ){
        cin >> s;
        if( s == 1 ){
            cin >> x >> y >> k;
            change(1, x, y, k);
            // cout << ask(1,1,n) << "\n" ;
        } else {
            cin >> x >> y;
            cout << ask(1, x, y) << "\n";
        }
    }
    return 0;
}
