matrix operator * (const matrix & a,const matrix & b){
    matrix c;
    for (int i=1; i<=9; i++)
        for (int j=1; j<=9; j++){
            c.m[i][j]=0;
            for (int k=1; k<=9; k++)
                c.m[i][j]=(c.m[i][j]+a.m[i][k]*b.m[k][j])%mod;//一般题目都要求要modulo一个数
        }
