}
matrix quick(matrix base,int pow){
    matrix a;
    for (int i=1; i<=9; i++) a.m[i][i]=1;
    while (pow)    {
        if (pow&1) a=a*base;
        base=base*base;
        pow>>=1;
    }
    return a;
}
