#### 最大流

```C++
typedef struct node{
    int to, cap, rev;
}edge;

bool vis[101];
vector <edge > G[101];
int dfs(int v, int t, int f){
    if(v == t) return f;
    vis[v] = 1;
    for(int i = 0; i < G[v].size(); i ++){
        edge e = G[v][i];
        if( !vis[e.to] && e.cap > 0 ){
            int d = dfs(e.to, t, min(f, e.cap));
            if( d > 0 ){
                e.cap -= d;
                G[e.to][e.rev].cap += d;
                return d;
            }
        }
    }
}

int max_flow(int s, int t){
    int flow = 0;
    while(true){
        memset(vis, 0, sizeof(vis));
        int f = dfs(s, t, INF);
        if( !f ) return flow;
        flow += f;
    }
}

int main(){
    int n, m;
    cin >> n >> m;
    int from, to, s = 0, t = n + m + 1;
    for(int i = 1; i <= m; i ++{
        G[s].push_back({i, 1, G[i].size()});
        G[i].push_back({s, 0, G[s].size() - 1});
    }
    for(int j = m + 1; j <= n + m; j ++){
        G[j].push_back({t, 1, G[t].size()});
        G[t].push_back({j, 0, G[j].size() - 1});
    }
    while ( scanf("%d%d", &from, &to), from != -1){
        G[to].push_back({from, 1, G[from].size()});
        G[from].push_back({to, 0, G[to].size() - 1});
    }
    cout << max_flow(s, t) << endl;
    return 0;
}
