**这个 有一个正解代码**
```C++
#include<bits/stdc++.h>
using namespace std;
const int N=1e3+10;
int dir[4][2]={-1,0,0,1,1,0,0,-1};
bool vis[N][N];
char mmp[N][N];
int n,flag;
bool check(int x,int y){//check if sea;
	for(int i=0;i<4;i++){
		int fx=x+dir[i][0];
		int fy=y+dir[i][1];
		if(fx&&fy&&fx<=n&&fy<=n&&mmp[fx][fy]=='.')
			return 1; //sea;
	}
	return 0; //no sea;
}
void dfs(int x,int y){
	vis[x][y]=1;
	if(!check(x,y))flag=1; //remain lu di
	for(int i=0;i<4;i++){
		int fx=x+dir[i][0];
		int fy=y+dir[i][1];
		if(fx&&fy&&fx<=n&&fy<=n&&!vis[fx][fy]&&mmp[fx][fy]=='#')
			dfs(fx,fy);
	}
}
int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		scanf("%s",mmp[i]+1);
	}
	memset(vis,0,sizeof(vis));
	int ans=0,ludi=0;
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++)
			if(!vis[i][j]&&mmp[i][j]=='#'){
            ludi++;
				flag=0; //yan mo
				dfs(i,j);
				if(flag) ans++; //remained
			}

	}
	printf("%d",ludi-ans);
}

```

