}
.page-navigator .page-number,
.page-navigator .extend {
  display: inline-block;
  margin: 0 5px 5px 0;
}
.page-navigator a,
.page-navigator span {
  display: inline-block;
  height: 25px;
  line-height: 25px;
  padding: 5px 9px;
  text-align: center;
  -webkit-box-shadow: 0 0 5px #adc2d7 inset;
  box-shadow: 0 0 5px #adc2d7 inset;
  border: 1px solid #adc2d7;
  background: rgba(0,0,0,0.8);
}
.page-navigator a:hover,
.page-navigator span:hover {
  border-color: #5483b1;
}
.page-navigator a.prev:before,
.page-navigator span.prev:before {
  font-family: "iconfont" !important;
  content: "\e00f";
  padding-right: 0.3em;
}
.page-navigator a.next:after,
.page-navigator span.next:after {
  font-family: "iconfont" !important;
  content: "\e00e";
  padding-left: 0.3em;
}
/* label title*/
.label-title {
  margin-top: 1.1em;
