}
.readmore a:hover {
  -webkit-box-shadow: 0 -1px 4px #5483b1 inset;
  box-shadow: 0 -1px 4px #5483b1 inset;
}
/* post navigator*/
.post-nav {
  overflow: hidden;
  margin-top: 15px;
  margin-bottom: 20px;
  padding: 10px;
  white-space: nowrap;
  border-top: 1px solid #d8e2eb;
}
.post-nav a {
  display: inline-block;
